import * as React from 'react';
import ToggleButton from '@mui/material/ToggleButton';
import ToggleButtonGroup from '@mui/material/ToggleButtonGroup';
import Tooltip from '@mui/material/Tooltip';
import French from '../../assests/images/french.png';
import English from '../../assests/images/english.png';
import { Box } from '@mui/material';

const DarkModeToggle = () => {
    const [alignment, setAlignment] = React.useState('left');
    const [alignment1, setAlignment1] = React.useState('left');

    const handleAlignment = (event, newAlignment) => {
        setAlignment(newAlignment);
    };
    const handleAlignment1 = (event, newAlignment) => {
        setAlignment1(newAlignment);
    };

    return (
      <Box display={'flex'}>
                <ToggleButtonGroup
                    defaultChecked={1}
                    value={alignment}
                    exclusive
                    size='small'
                    onChange={handleAlignment}
                    aria-label="text alignment"
                >
                    <Tooltip title="FranÃ§ais">
                        <ToggleButton value="left" aria-label="left aligned">
                            <img src={French} width={30} />
                        </ToggleButton>
                    </Tooltip>
                    <Tooltip title="English">
                        <ToggleButton value="center" aria-label="centered">
                            <img src={English} width={22} />
                        </ToggleButton>
                    </Tooltip>
                    {/* <Tooltip title="Darck Mode">
            <ToggleButton value="left" aria-label="left aligned">
                ðŸŒ™
            </ToggleButton>
            </Tooltip  >
            <Tooltip title="Light Mode">
            <ToggleButton value="center" aria-label="centered">
                ðŸ”†
            </ToggleButton>
            </Tooltip> */}
                </ToggleButtonGroup>

                <ToggleButtonGroup
                    defaultChecked={1}
                    value={alignment1}
                    exclusive
                    size='small'
                    onChange={handleAlignment1}
                    aria-label="text alignment"
                >
                    {/* <Tooltip title="FranÃ§ais">
           <ToggleButton value="left" aria-label="left aligned">
               <img src={French} width={30} />
           </ToggleButton>
           </Tooltip>
           <Tooltip title="English">
           <ToggleButton value="center" aria-label="centered">
               <img src={English} width={22} />
           </ToggleButton>
           </Tooltip> */}
                    <Tooltip title="Darck Mode">
                        <ToggleButton value="left" aria-label="left aligned">
                            ðŸŒ™
                        </ToggleButton>
                    </Tooltip  >
                    <Tooltip title="Light Mode">
                        <ToggleButton value="center" aria-label="centered">
                            ðŸ”†
                        </ToggleButton>
                    </Tooltip>
                </ToggleButtonGroup>
                </Box >
            );
}

